INSERT INTO `help` (`ID`, `help_group_ID`, `header`, `description`) VALUES
(5, 3, 'Csevegés indítása', '1.) Legegyszerűbben a baloldali navigációs sávon található „Ház” ikonra kattintva teheti ezt meg, ahol a megjelenő felhasználókra való kattintás után tud beszélgetést kezdeményezi az adott felhasználó irányába.'),
(6, 3, NULL, '2.) Ha nincs megkezdett beszélgetése kattintson a baloldali navigációs sávon található „Üzenetek” ikonra, ahol a „Csevegés megkezdése” gombra kattintva, a program a főoldalra navigálja, ahol a megjelenő felhasználókra való kattintás után tud beszélgetést kezdeményezi az adott felhasználó irányába.'),
(7, 3, NULL, '3.) Ha van megkezdett beszélgetése kattintson a baloldali navigációs sávon található „Üzenetek” ikonra, ahol a lent található zöld plusz gombra kattintva, a program a főoldalra navigálja, ahol a megjelenő felhasználókra való kattintás után tud beszélgetést kezdeményezi az adott felhasználó irányába.'),
(8, 3, 'Csevegés törlése', 'A baloldali navigációs sávon található „Üzenetek” ikonra kattintva betöltődnek a meglévő üzenetei, itt kattintással válassza ki a megkezdett beszélgetései közül azt amelyiket törölni szeretné, majd a jobbfelső sávon található ⁞-ra kattintva találja az üzenet törése lehetőséget. '),
(9, 3, '', 'A törölt beszélgetések mindkét félnél törlésre kerülnek.'),
(12, 1, 'A támogatott operációs rendszerekről', 'A program futtatásához .Net Core 3.1 vagy újabb keretrendszer szükséges. A keretrendszert telepítve az alábbi operációs rendszerek a támogatottak: Windows, macOS és Linux. Támogatott Linux disztribúciókról az alábbi oldalon olvashat többet: https://docs.microsoft.com/en-us/dotnet/core/install/linux'),
(13, 1, 'Program letöltése', 'A program telepítőjét az http://www.chatapp.hu/download.html oldalon tudja letölteni.'),
(14, 1, 'Program telepítése', ''),
(15, 1, 'Program törlése', 'Windows esetében,\r\nVezérlőpult → Program eltávolítása → „CHAT APP” -ra duplán kattintva feljön az Uninstall Setup, tovább-továbbra kattintva törlődik a számítógépről a program.'),
(16, 1, '', 'Linux esetében,\r\nTörölni a „apt-get remove CHAT APP” futtatásával lehet, de az „apt-get purge CHAT APP” paranccsal az adott program összes konfigurációs fájlja is törlésre kerül.'),
(17, 1, '', 'macOS esetében,\r\nA Finder-ablak oldalsávján az alkalmazások elemre kattintva megjelennek a telepített alkalmazások, itt válassza ki a „CHAT APP” -ot majd húzza a Kukába, vagy Fájl → Áthelyezés a kukába. Ha a rendszer a felhasználónevet és a jelszót kéri, adja meg a Mac egyik rendszergazdai fiókjához használt nevet és jelszót. Ez valószínűleg az a név és jelszó, amelyet a Macbe történő bejelentkezéshez használ. Az alkalmazás törléséhez válassza ki a Finder → Kuka ürítése elemet.'),
(18, 2, 'Regisztráció', 'Alkalmazás indításakor válasza a „Még nincs fiókod? Kattints ide” lehetőséget, hogy a program a regisztrációs felületre navigálja. A két lépcsős regisztráció első felében megkell adnia a belépéshez szükséges adatait, a második felében pedig személyi profiljához szükséges adatait.'),
(19, 2, 'Belépés', 'Alkalmazás indításakor töltse ki a bejelentkezési űrlapot, ahol meg kell adnia vagy az E-mail címét és jelszavát, vagy felhasználónevét és jelszavát. Ha az emlékezz rám lehetőség be van pipálva addig amíg ki nem jelentkezi abból, a program indításkor automatikusan bejelentkezteti.'),
(20, 2, 'Kijelentkezés', 'A baloldali navigációs sávon található legalsó ikonra kattintva teheti ezt meg.'),
(21, 2, 'E-mail módosítása', 'A baloldali navigációs sávon található „Fogaskerék” ikonra kattintva, a program átnavigálja a beállításokra, ahol a „Fiók beállítása” → „E-mail” menüpontot választva lesz lehetősége ezt a megtenni.'),
(22, 2, 'Felhasználónév módosítása', 'Felhasználónév módosítására nincsen lehetőség.'),
(23, 2, 'Jelszó módosítása', 'A baloldali navigációs sávon található „Fogaskerék” ikonra kattintva, a program átnavigálja a beállításokra, ahol a „Fiók beállítása” → „Jelszó” menüpontot választva lesz lehetősége ezt a megtenni.'),
(24, 2, 'Profilkép módosítása', 'A baloldali navigációs sávon található „Fogaskerék” ikonra kattintva, a program átnavigálja a beállításokra, ahol a „Profil szerkesztése” menüpontot választva lesz lehetősége ezt a megtenni.'),
(25, 2, 'Fiók törlése', 'A baloldali navigációs sávon található „Fogaskerék” ikonra kattintva, a program átnavigálja a beállításokra, ahol a „Fiók beállítása” → „Fiók törlése” menüpontot választva lesz lehetősége ezt a megtenni.'),
(31, 3, 'Üzenetek titkosításának bekapcsolása', 'A baloldali navigációs sávon található „Fogaskerék” ikonra kattintva, a program átnavigálja a beállításokra, ahol a „Profil szerkesztése” menüpontot választva lesz lehetősége ezt a szolgálatást bekapcsolnia, illetve kikapcsolnia.'),
(32, 4, 'Elküldött üzeneteket vissza lehet vonni?', 'Elküldött üzenetek visszavonására nincs lehetőség, csak teljes beszélgetések törlésére, melyet fentebb olvashat.'),
(33, 4, 'Hogyan tudok gyorsan megtalálni a beszélgetésekeim között egy régebbi megkezdett beszélgetésemet?', 'Az üzenetek felülete tartalmaz egy keresőt felül, amely megkönnyíti megtalálni a megkezdett csevegéseket. Keresni a felhasználó neve alapján lehet.'),
(34, 4, 'Ha az üzenetek titkosítása szolgálatás be van kapcsolva, akkor a beszélgetéseim teljesen titkosítva lesznek?', 'Ahhoz, hogy a jelenlegi vagy az ezt követő csevegések teljesen titkosítva legyen, a beszélgetésben részvevő mindkét félnek használnia kell a CHAT APP ezen szolgálatását, ellenkező esetben csak az Ön üzenetei lesznek titkosítva küldve, tárolva, és fogadva.'),
(35, 4, 'Honnan szerezhetem be a .Net Core keretrendszer szükséges verzióját?', 'Ezt az alábbi oldalon teheti meg: https://dotnet.microsoft.com/download/dotnet-core'),
(36, 4, 'Ismerősömre rá szeretnék írni, hogyan tudok rákeresni?', 'A baloldali navigációs sávon található „Ház” ikonra kattintva teheti ezt meg, ahol a keresőt használva egyszerűen megtalálható a név alapján a keresendő felhasználó, majd kattintson rá, hogy beszélgetést tudjon kezdeményezi az adott fél irányába.'),
(37, 4, 'A kezdőlapon a felhasználók között keresve miért nem látom, hogy ki mikor volt online?', 'Csak az üzenetek felületen lehet látni a felhasználók online státuszát.'),
(38, 4, 'Az „üzenetek” ikonnál a piros számláló micsoda?', 'A megválaszolatlan üzenetek számát jelöli.'),
(39, 4, 'Hol tudom megtekinteni a program aktuális verzióját?', 'A baloldali navigációs sávon található „Fogaskerék” ikonra kattintva, a program átnavigálja a beállításokra, ahol a „Névjegy” menüpontot választva lesz lehetősége ezt megtekinteni.');

