INSERT INTO `privacy_policy` (`ID`, `header`, `description`) VALUES
(1, 'Adatvédelem', 'Fontos számunkra, hogy jól érezze magát és megbízhasson bennünk amikor a CHAT APP-ot, mint szolgáltatást használja. Kérjük, szánjon néhány percet az adatvédelmi irányelvek elolvasására, hogy megértse, milyen információkat gyűjtünk, mit csinálunk velük és miért, miként védjük és milyen jogai vannak az információkkal kapcsolatban.'),
(2, 'Információ gyűjtés', 'Csak és kizárólag olyan információkat gyűjtünk, amelyek lehetővé teszik számunkra, hogy az alapvető szolgáltatásokat biztosítani tudjuk számára. Ide tartozik például olyan egyszerűbb feladat, mint például annak lehetővé tétele, hogy más felhasználók láthassák a megjeleníteni kívánt nevet és képet. '),
(3, NULL, 'A CHAT APP üzenetek titkosítása funkció végpontok közötti biztonságos kommunikációcserét tesz lehetővé, amely a beállításokon belül opcionálisan választható lehetőség. '),
(4, 'Üzenetek titkosítása', 'Polialfabetikus-rejtjelezést használunk az üzenetek titkosításához. A CHAT APP egy tovább gondolt Vigenère-rejtjel titkosítási módszert használ, mely az eredeti 26 sorból és 26 oszlopból álló táblázat helyett a Unicode-tábla karaktereit használja a rejtjelezéshez. Ezzel képezve egy 137 994 x 137 994 -es mátrixot, amely lehetővé teszi a felhasználóinknak a magasabb fokú személyes adatbiztonságot.'),
(5, 'Adatmegőrzés', 'Adatait csupán addig őrizzük, amíg az szükséges, és a felhasználó nem dönt úgy, hogy törölni szeretné a beszélgetéseit vagy komplett személyi fiókját. Fiók törlésekor személyes adatai, beszélgetései továbbá bárminemű Ön által generált egyéb adatok az utolsó bájtig törlődnek szervereinkről. Ezzel biztosítja a CHAT APP, felhasználói számára a privát egyén megőrzésését.'),
(6, 'Titoktartás', 'Adatait a tőlünk telhető legnagyobb biztonságban tároljuk, melyeket 99,7%-ban nem osztunk meg harmadik felekkel.'),
(7, 'A SZABÁLYZAT MÓDOSÍTÁSA', 'Jelen Szabályzatot meghatározott időközönként felülvizsgáljuk és módosítjuk annak aktualitása érdekében. '),
(8, '', 'Javasoljuk, hogy időről-időre nézze át jelen Szabályzatot annak érdekében, hogy személyes adatainak kezelésével kapcsolatos minden tény és információ tekintetében naprakész legyen.'),
